import { Plugin } from 'vite';

interface Options {
    fragments?: string;
    /**
     * The graphql schema. If a url is provided, the schema will be automatically fetched.
     * Otherwise is will load the schema from the local file system.
     */
    schema?: string;
    /**
     * Path for automatically generated schema types.
     */
    dts?: string;
    components?: string;
}
declare type UserOptions = Partial<Options>;

declare function VitePluginVueGQL(options?: UserOptions): Plugin;

export default VitePluginVueGQL;
